const Shop = (props) => {
    const firstTen = data.slice(0, 10);
    console.log(firstTen);
    const [products, setProduct] = useState(firstTen);
    const [cart, setCart] = useState([]);
   
    const handleClick = (products) =>{
        console.log('product Added', products);
        
        

    }
    
   
    
    

    return (
        <div className='shop_container'>
            <div className="product_container">
           
            {
                products.map(pd => <Product handleClick={products.handleClick(props.products)} product ={pd}></Product>)
            }
           

            </div>
            <div className="cart_conatiner">
                <Carts cart = {cart}></Carts>
                
                
              
            </div>
          
        
            
        </div>
    );
};

export default Shop;
