import React from 'react';
import './Cart.css';

const Cart = (props) => {
    const cart = props.cart;
    // const totalPrice = cart.reduce((total, prd) => total + prd.price, 0); //Shortcut version 
    let total = 0; //long process
    for (let i = 0; i< cart.length; i++) {
      const product = cart[i];
      total = total + product.price;
       
    }
    let shipping = 0;
    if(total > 350){
        shipping = 15;
    }
    else if (total > 150){
        shipping = 5;
    }else if (total > 0){
        shipping = 10;
    }

    const tax = Math.round(total / 10);
    
    return (
        <div>
            <h4>Order Summary: </h4>
            <p>Items Ordered: {cart.length}</p>
            <p>Shipping: {shipping}</p>
            <p>TAX/Vat: {tax}</p>
            <p>Total Price: {total + shipping + tax}</p>
            <button className='Order_btn'>Place Order</button>
        </div>
    );
};

export default Cart;
