import React from 'react';
import './Cart.css';

const Cart = (props) => {
    const cart = props.cart;
    const totalPrice = cart.reduce((total, prd) => total + prd.price, 0);
 
    
    return (
        <div>
            <h4>order Summary</h4>
            <p>Items ordered: {cart.length}</p>
            <p>TotalPrice: {totalPrice}</p>

        </div>
    );
};

export default Cart;
