import React from 'react';
import './Cart.css';

const cart = (props) => {
    let total = 0;
    for(let i = 0; i< cart.length; i++){
        const product = cart[i];
        total = total + product.price;
    }
    const cart = props.cart;
    return (
        <div>
            <h4>Order Summary: </h4>
            <p>Items Ordered: {cart.length}</p>
        </div>
    );
};

export default cart;
